import React, { Component } from 'react'
import faqImage from "../assets/constructed/Faq_graphic.png";
import dropdown from "../assets/dropdown.png"
import hopper from "../assets/Hopper.png";
import haply from "../assets/Haply.png";
import notos from "../assets/Notos Technologies.png";
import data from "../assets/Dataperformers.png";
import { Slide } from "react-slideshow-image";
import karl from "../assets/Karl_Atallah.jpeg";
import shirly from "../assets/Shirly_Xu.jpeg";

export class faq extends Component {
  state = {
    index: 0,
    direction: null,
  };

  handleSelect(selectedIndex, e) {
    this.setState({
      index: selectedIndex,
      direction: e.direction,
    });
  }

  render() {

    const properties = {
      controls: true,
      indicators: true,
      duration: 7000,
      infinite: true,
    };

    return (
      <div id="faq">
      <div className="faqTitle">Hear from our <span style={{color:"#ff0000"}}>Participants</span></div>
        <div className="faqContainer">
            <div className="faqImgColumn">
                <img src={faqImage} className="faqImage" alt="FAQ city scape"></img>
            </div>
          <div className="faqTextColumn">
          <div className="participantBox">
          <Slide {...properties}>
            <div className="each-slide">
              <div className="participantSlide">
                  <img src={karl} className="participantImage" alt="participant Karl's image" />
                <p className="hopperText">
                “The NetX Event combines aspects of a traditional consulting case, a tech hackathon, an engineering competition, and a networking experience all in an action-packed weekend. Participating at the event was one of the most enriching and insightful experiences in my engineering degree at McGill University so far.”
                </p>
              </div>
            </div>
            <div className="each-slide">
              <div className="participantSlide">
                    <img
                      src={shirly}
                      className="participantImage"
                      alt=""
                    />
                  <p className="notosText">
                  "NetX was an incredibly rewarding case competition and networking session to be a part of. 
 
 This was one of the best and most well-organized networking sessions I've attended (online due to COVID). The speakers were very welcoming, and the breakout rooms were filled with lively and insightful discussions. NetX does a fantastic job of giving participants a unique opportunity to network with successful tech companies and CEOs, and relating the cases directly to possible problems experienced by the invited startups. While the competition only lasts the weekend, the meaningful connections you form and the skills you hone in extend far beyond. I would definitely recommend NetX to all, especially to those interested in tech and business!
  
 A huge congrats to the organizing team for putting together the event from start to finish. They are also very responsive and willing to help us out!"
 
                  </p>
                </div>
            </div>
          </Slide>
        </div>
        </div>
          </div>
      </div>
    )
  }
}

export default faq
